DROP TABLE pelicula_movie;
CREATE TABLE pelicula_movie(
   id                 INTEGER  NOT NULL PRIMARY KEY AUTO_INCREMENT
  ,budget             INTEGER
  ,genre              VARCHAR(32)
  ,original_language  VARCHAR(16)
  ,original_title     VARCHAR(128) NOT NULL
  ,overview           VARCHAR(1024)
  ,production_company VARCHAR(128)
  ,production_country VARCHAR(128)
  ,release_date       DATE 
  ,revenue            NUMERIC(12,2)
  ,runtime            NUMERIC(6,2)
);
